import{_ as e,c as a,a2 as r,o as n}from"./chunks/framework.D6xVe7cl.js";const p=JSON.parse('{"title":"发行说明","description":"","frontmatter":{},"headers":[],"relativePath":"preface/release.md","filePath":"preface/release.md","lastUpdated":1739089174000}'),l={name:"preface/release.md"};function o(s,t,d,h,i,c){return n(),a("div",null,t[0]||(t[0]=[r('<h1 id="发行说明" tabindex="-1">发行说明 <a class="header-anchor" href="#发行说明" aria-label="Permalink to &quot;发行说明&quot;">​</a></h1><nav class="table-of-contents"><ul><li><a href="#版本方案">版本方案</a><ul><li><a href="#命名参数">命名参数</a></li><li><a href="#弃用-改进-向下兼容">弃用 &amp; 改进 &amp; 向下兼容</a></li></ul></li><li><a href="#支持策略">支持策略</a></li></ul></nav><h2 id="版本方案" tabindex="-1">版本方案 <a class="header-anchor" href="#版本方案" aria-label="Permalink to &quot;版本方案&quot;">​</a></h2><p><strong>Waplar</strong> 及其第一方包遵循 <a href="https://semver.org/" target="_blank" rel="noreferrer">语义化版本</a> 控制。主要版本每年发布一次（约第一季度），次要版本每月发布一次（1号至5号之间），补丁版本每周发布一次（根据 <code>Issue</code> 和实际情 况决定），次要版本和补丁版本不会存在 <strong>重大更改</strong> （无法向下兼容的情况）。</p><p>从应用程序或包中引入 <strong>Waplar</strong> 或组件时，应始终使用版本约束（例如）<code>^1.0.0-alpha</code> 或是 <code>^1.x</code>，因为 <strong>Waplar</strong> 的主要版本确实包含重大更改。但是，我们始终努力确保您可以第一时间内更新到 最新的主要版本。</p><h3 id="命名参数" tabindex="-1">命名参数 <a class="header-anchor" href="#命名参数" aria-label="Permalink to &quot;命名参数&quot;">​</a></h3><p><a href="https://www.php.net/manual/en/functions.arguments.php#functions.named-arguments" target="_blank" rel="noreferrer">命名参数</a> 不在 <strong>Waplar</strong> 的向后兼容性指南的范围内。我们可能会在必要时选择重命名函数参数，以改进 <strong>Waplar</strong> 代码库。因此，在调用 <strong>Waplar</strong> 方法时使用命名参数应该谨慎进行，并了解参数名称将来可能会更改。</p><h4 id="实验性" tabindex="-1">实验性 <a class="header-anchor" href="#实验性" aria-label="Permalink to &quot;实验性&quot;">​</a></h4><p>在次要版本及补丁版本中我们会加入一些实验性功能并在文档中标记，当三性（<strong>可用性</strong>、<strong>可靠性</strong>、<strong>可维护性</strong> ）符合时会正式纳入主要版本，如果在当前版本生命周期内无法保证三性将会考虑在下一个主要版本移除。</p><h3 id="弃用-改进-向下兼容" tabindex="-1">弃用 &amp; 改进 &amp; 向下兼容 <a class="header-anchor" href="#弃用-改进-向下兼容" aria-label="Permalink to &quot;弃用 &amp; 改进 &amp; 向下兼容&quot;">​</a></h3><p>在每个主要版本的生命周期末的最后一个季度会公示下一个主要版本选择弃用的功能，并在下一个主要版本彻底移除。如果仅是对该功能的改进，则会在次要版本中使用全新的命名以兼容原有的功能，改进后的功能会被标记为实验性，并在下一个主要版本加入。</p><h2 id="支持策略" tabindex="-1">支持策略 <a class="header-anchor" href="#支持策略" aria-label="Permalink to &quot;支持策略&quot;">​</a></h2><p>对于所有 <strong>Waplar</strong> 版本，<code>bug</code> 修复提供 <strong>12</strong> 个月，安全修复提供 <strong>18</strong> 个月。<strong>Waplar</strong> 及其第一方包只有最新版本会承诺 <code>bug</code> 修复，此外，请注意 <a href="https://laravel.com/docs/11.x/database#introduction" target="_blank" rel="noreferrer">Laravel</a> 能够支持的数据库版本。</p><table tabindex="0"><thead><tr><th style="text-align:center;">版本</th><th style="text-align:center;">PHP(^)</th><th style="text-align:center;">Laravel(~)</th><th style="text-align:center;">发布</th><th style="text-align:center;">错误修复截止日期</th><th style="text-align:center;">安全修复截止日期</th></tr></thead><tbody><tr><td style="text-align:center;">alpha</td><td style="text-align:center;">8.2.x</td><td style="text-align:center;">11.x</td><td style="text-align:center;">-</td><td style="text-align:center;">-</td><td style="text-align:center;">-</td></tr></tbody></table><p><strong>(^)</strong> 支持的 PHP 版本， <strong>(~)</strong> 支持的 Laravel 版本</p>',15)]))}const f=e(l,[["render",o]]);export{p as __pageData,f as default};
